```python
from collections import Counter
import math

def calculate_bleu_score(list_of_references, hypotheses) -> float:
    def count_ngrams(tokens, n):
        ngrams = zip(*[tokens[i:] for i in range(n)])
        return Counter(ngrams)

    def calculate_precision(candidate, reference, n):
        candidate_ngrams = count_ngrams(candidate, n)
        reference_ngrams = count_ngrams(reference, n)
        overlap = sum(min(candidate_ngrams[k], reference_ngrams[k]) for k in candidate_ngrams)
        total = sum(candidate_ngrams.values())
        return overlap / total if total > 0 else 0

    def brevity_penalty(candidate, references):
        candidate_length = len(candidate)
        closest_ref_length = min(len(ref) for ref in references)
        if candidate_length > closest_ref_length:
            return 1
        else:
            return math.exp(1 - closest_ref_length / candidate_length)

    def geometric_mean(precisions):
        return (math.prod(precisions)) ** (1 / len(precisions))

    bleu_scores = []
    for references, hypothesis in zip(list_of_references, hypotheses):
        precisions = [calculate_precision(hypothesis, ref, n) for n in range(1, 5)]
        brevity = brevity_penalty(hypothesis, references)
        bleu = brevity * geometric_mean(precisions)
        bleu_scores.append(bleu)

    return round(sum(bleu_scores) / len(bleu_scores), 4)
```

