```java
import java.util.Stack;

class Solution {
    public int calPoints(String[] ops) {
        Stack<Integer> stack = new Stack<>();
        for (String str : ops) {
            if (str.equals("C")) {
                stack.pop(); // Remove the last valid point
            } else if (str.equals("D")) {
                stack.push(stack.peek() * 2); // Double the last valid point and add to the stack
            } else if (str.equals("+")) {
                int top = stack.pop(); // Get the last valid point
                int newTop = top + stack.peek(); // Add the last two valid points
                stack.push(top); // Restore the last valid point
                stack.push(newTop); // Add the sum of the last two valid points to the stack
            } else {
                stack.push(Integer.parseInt(str)); // Add the integer to the stack
            }
        }
        int sum = 0;
        for (int score : stack) {
            sum += score; // Calculate the sum of all valid points
        }
        return sum;
    }
}
```

