import struct

class Message:
    def __init__(self, data):
        self.data = data

    def serialize(self):
        """
        serialize the message data into a byte array
        :return: byte array of serialized message, ``bytes``
        """
        try:
            # Assuming data is a string, encoding it to bytes
            return self.data.encode('utf-8')
        except (struct.error, TypeError) as e:
            raise ValueError(f"{type(e)}: '{e}' when writing '{self.data}'")

    def deserialize(self, data):
        """
        unpack serialized message in data into this message instance
        :param data: byte array of serialized message, ``bytes``
        """
        if isinstance(data, bytes):
            # Assuming data is encoded in utf-8, decoding it to string
            self.data = data.decode('utf-8')
        else:
            raise ValueError("Invalid data type for deserialization")
