import java.util.Arrays;

public class ClassUtilTest {
    public ClassUtilTest() {
    }

    public String getClassSimpleName(Class<?> clazz) {
        return clazz.getSimpleName();
    }

    public boolean isClassAbstract(Class<?> clazz) {
        return java.lang.reflect.Modifier.isAbstract(clazz.getModifiers());
    }

    public String getSuperclassSimpleName(Class<?> clazz) {
        Class<?> superclass = clazz.getSuperclass();
        return superclass != null ? superclass.getSimpleName() : "None";
    }

    public String[] getAllInterfaces(Class<?> clazz) {
        Class<?>[] interfaces = clazz.getInterfaces();
        String[] interfaceNames = new String[interfaces.length];
        for (int i = 0; i < interfaces.length; i++) {
            interfaceNames[i] = interfaces[i].getSimpleName();
        }
        return interfaceNames;
    }
}
