def find_invalid_providers(config_file: str) -> List[Tuple[str, str]]:
    with open(config_file, 'r') as file:
        config = yaml.safe_load(file)

    invalid_providers = []
    for engine in config['engines']:
        provider = engine.get('_provider')
        if not provider or '.' not in provider:
            invalid_providers.append((engine['name'], 'NotImplementedError'))
        else:
            provider_class = provider.split('.')[-1]
            try:
                exec(f'from {provider.rsplit(".", 1)[0]} import {provider_class}')
            except ImportError:
                invalid_providers.append((engine['name'], 'ImportError'))

    return invalid_providers
