def format_list(input_str):
    lines = input_str.strip().split('\n')
    formatted_list = []
    current_indent = 0
    for line in lines:
        indent_level = line.count('\t')
        item = line.strip('* \t')
        if indent_level > current_indent:
            formatted_list.append(item)
        elif indent_level == current_indent:
            formatted_list[-1] += '\n' + '    ' * indent_level + '- ' + item
        else:
            diff = current_indent - indent_level
            formatted_list[-1 - diff] += '\n' + '    ' * (indent_level) + '- ' + item
        current_indent = indent_level
    return '\n'.join(formatted_list)
