import java.io.*;
import java.nio.charset.StandardCharsets;

public class CacheManager {
    public void writeContentAsLatin1(String filePath, String content) {
        try (Writer writer = new OutputStreamWriter(new FileOutputStream(filePath), StandardCharsets.ISO_8859_1)) {
            writer.write(content);
        } catch (IOException e) {
            // Handle or log the exception
        }
    }

    public String getValue(String key) throws IllegalStateException {
        try {
            byte[] content = readContent(key);
            return new String(content, StandardCharsets.ISO_8859_1);
        } catch (IOException e) {
            throw new IllegalStateException("Error reading cache file: " + e.getMessage());
        }
    }

    private byte[] readContent(String filePath) throws IOException {
        try (InputStream inputStream = new FileInputStream(filePath)) {
            byte[] buffer = new byte[inputStream.available()];
            int bytesRead = inputStream.read(buffer);
            if (bytesRead < 0) {
                throw new IOException("Empty file");
            }
            return buffer;
        }
    }
}
