from typing import List, Tuple

def find_first_collision(grid: List[List[str]]) -> Tuple[int, int]:
    directions = {'^': -1j, 'v': 1j, '<': -1, '>': 1}
    turns = {'left': -1j, 'straight': 1, 'right': 1j}
    carts = []

    for y, row in enumerate(grid):
        for x, cell in enumerate(row):
            if cell in directions:
                carts.append((x + y * 1j, directions[cell], 'left'))

    def move_cart(cart):
        pos, direction, turn = cart
        new_pos = pos + direction
        new_cell = grid[int(new_pos.imag)][int(new_pos.real)]

        if new_cell in directions:
            return new_pos, direction, turn

        if new_cell == '+':
            direction *= turns[turn]
            turn = ('left', 'straight', 'right', 'left')[('left', 'straight', 'right', 'left').index(turn) - 1]

        if new_cell == '/':
            direction *= -1 if direction in (1, -1j) else 1

        if new_cell == '\\':
            direction *= -1 if direction in (-1, -1j) else 1

        return new_pos, direction, turn

    positions = set()
    for cart in carts:
        if cart[0] in positions:
            return (int(cart[0].real), int(cart[0].imag))
        positions.add(cart[0])

    while True:
        carts = [move_cart(cart) for cart in sorted(carts, key=lambda c: (c[0].imag, c[0].real))]
        positions = set()
        for cart in carts:
            if cart[0] in positions:
                return (int(cart[0].real), int(cart[0].imag))
            positions.add(cart[0])
