import java.util.Objects;

public class DataAnnotationGenerator {
    public static void main(String[] args) {
        generateDataAnnotation("Person", new Field("id", "int"), new Field("name", "String"), new Field("age", "int"));
    }

    public static void generateDataAnnotation(String className, Field... fields) {
        StringBuilder classCode = new StringBuilder();
        classCode.append("public class ").append(className).append(" {\n");

        for (Field field : fields) {
            classCode.append("    private ").append(field.getDataType()).append(" ").append(field.getFieldName()).append(";\n");
            classCode.append("\n");
            classCode.append("    public ").append(field.getDataType()).append(" get").append(capitalize(field.getFieldName())).append("() {\n");
            classCode.append("        return ").append(field.getFieldName()).append(";\n");
            classCode.append("    }\n");
            classCode.append("\n");
            classCode.append("    public void set").append(capitalize(field.getFieldName())).append("(").append(field.getDataType()).append(" ").append(field.getFieldName()).append(") {\n");
            classCode.append("        this.").append(field.getFieldName()).append(" = ").append(field.getFieldName()).append(";\n");
            classCode.append("    }\n");
            classCode.append("\n");
        }

        classCode.append("    @Override\n");
        classCode.append("    public boolean equals(Object o) {\n");
        classCode.append("        if (this == o) return true;\n");
        classCode.append("        if (o == null || getClass() != o.getClass()) return false;\n");
        classCode.append("        ").append(className).append(" ").append(className.toLowerCase()).append(" = (").append(className).append(") o;\n");
        for (Field field : fields) {
            classCode.append("        if (").append(field.getFieldName()).append(" != ").append(className.toLowerCase()).append(".").append(field.getFieldName()).append(") return false;\n");
        }
        classCode.append("        return true;\n");
        classCode.append("    }\n");
        classCode.append("\n");

        classCode.append("    @Override\n");
        classCode.append("    public int hashCode() {\n");
        classCode.append("        return Objects.hash(");
        for (int i = 0; i < fields.length; i++) {
            classCode.append(fields[i].getFieldName());
            if (i < fields.length - 1) {
                classCode.append(", ");
            }
        }
        classCode.append(");\n");
        classCode.append("    }\n");
        classCode.append("\n");

        classCode.append("    @Override\n");
        classCode.append("    public String toString() {\n");
        classCode.append("        return \"").append(className).append("{\" +\n");
        for (int i = 0; i < fields.length; i++) {
            classCode.append("                \"").append(fields[i].getFieldName()).append("='\" + ").append(fields[i].getFieldName()).append(" + '\\''");
            if (i < fields.length - 1) {
                classCode.append(" +\n");
            } else {
                classCode.append(" +\n");
            }
        }
        classCode.append("                '}';\n");
        classCode.append("    }\n");

        classCode.append("}\n");

        System.out.println(classCode.toString());
    }

    private static String capitalize(String str) {
        return str.substring(0, 1).toUpperCase() + str.substring(1);
