    for file in os.listdir(source_dir):
        if os.path.isfile(os.path.join(source_dir, file)):
            ext = os.path.splitext(file)[1][1:].lower()
            src_path = os.path.join(source_dir, file)
            dest_path = os.path.join(source_dir, ext, file)
