from datetime import timedelta

def _parse_offset(raw_offset):
    """Parse the offset."""
    if raw_offset is None:
        return timedelta(0)

    negative_offset = False
    if raw_offset.startswith('-'):
        negative_offset = True
        raw_offset = raw_offset[1:]  # Remove the negative sign

