from typing import List

class PaginationHelper:
    def __init__(self, collection: List[int], items_per_page: int):
        self.collection = collection
        self.items_per_page = items_per_page

    def item_count(self) -> int:
        return len(self.collection)

    def page_count(self) -> int:
        return -(-len(self.collection) // self.items_per_page)

    def page_item_count(self, page_index: int) -> int:
        if page_index < 0 or page_index >= self.page_count():
            return -1
        if page_index == self.page_count() - 1:
            return len(self.collection) % self.items_per_page or self.items_per_page
        return self.items_per_page

    def page_index(self, item_index: int) -> int:
        if item_index < 0 or item_index >= len(self.collection):
            return -1
        return item_index // self.items_per_page
