def apply_migrations(initial_schema, migrations):
    final_schema = initial_schema.copy()

    for operation, details in migrations:
        if operation == 'CreateModel':
            model_name, fields = details
            final_schema[model_name] = fields
        elif operation == 'RenameModel':
            old_name, new_name = details
            final_schema[new_name] = final_schema.pop(old_name)
        elif operation == 'AddField':
            model_name, field_name = details
            final_schema[model_name].append(field_name)
        elif operation == 'RemoveField':
            model_name, field_name = details
            final_schema[model_name].remove(field_name)

    return final_schema
