from typing import List, Any
import random

class SegmentedReplayBuffer:
    def __init__(self, segment_capacities: List[int]):
        self.segments = [[] for _ in range(len(segment_capacities))]
        self.segment_capacities = segment_capacities

    def add_experience(self, experience: Any, priority: int) -> None:
        segment_index = self._get_segment_index(priority)
        if len(self.segments[segment_index]) < self.segment_capacities[segment_index]:
            self.segments[segment_index].append(experience)
        else:
            # Replace a random experience in the segment
            replace_index = random.randint(0, self.segment_capacities[segment_index] - 1)
            self.segments[segment_index][replace_index] = experience

    def sample_batch(self, batch_size: int) -> List[Any]:
        sampled_batch = []
        for segment in self.segments:
            if len(segment) > 0:
                batch_size_per_segment = min(batch_size, len(segment))
                sampled_batch.extend(random.sample(segment, batch_size_per_segment))
                batch_size -= batch_size_per_segment
                if batch_size == 0:
                    break
        return sampled_batch

    def _get_segment_index(self, priority: int) -> int:
        # Example: If priorities are [10, 20, 30] and priority is 25, it should return 1 (index of segment with priority 20)
        for i, capacity in enumerate(self.segment_capacities):
            if priority <= capacity:
                return i
        return len(self.segment_capacities) - 1  # Return the last segment index if priority exceeds all capacities
