import torch
from PIL import Image

def process_input(img, flat_label, branch_label=None, depth=None, transform=None):
    if branch_label is not None:
        flat_label = torch.tensor(flat_label)
        branch_label = torch.tensor(branch_label)
        pad = torch.tensor([-100] * (depth - branch_label.size(0)), dtype=torch.int64)
        branch_label = torch.cat((branch_label, pad), 0)
        img = Image.fromarray(img.numpy().T, mode='L')
        input_data = {'img': img, 'label': flat_label, 'branch_label': branch_label}
    else:
        img = Image.fromarray(img.numpy().T, mode='L')
        label = torch.tensor(flat_label)
        input_data = {'img': img, 'label': label}

    if transform is not None:
        input_data = transform(input_data)

    return input_data
```

