function isBalancedParentheses(s) {
  const stack = [];
  const opening = '(';
  const closing = ')';
  for (let char of s) {
    if (char === opening) {
      stack.push(char);
    } else if (char === closing) {
      if (stack.length === 0) {
        return false; // Unmatched closing parenthesis
      }
      stack.pop();
    }
  }
  return stack.length === 0; // If stack is empty, all parentheses are matched
}

