from collections import deque

class CircularBuffer:
    def __init__(self, capacity):
        self.capacity = capacity
        self.buffer = deque()

    def append(self, value):
        if len(self.buffer) == self.capacity:
            self.buffer.popleft()
        self.buffer.append(value)

    def get_snippet(self, snippet_lines_increment, snippet_lines_coverage):
        if len(self.buffer) == self.capacity:
            for i in range(snippet_lines_increment):
                yield list(self.buffer)[:snippet_lines_increment]
```

