import org.springframework.expression.Expression;
import org.springframework.expression.ExpressionParser;
import org.springframework.expression.spel.standard.SpelExpressionParser;

public class SpelEvaluator {
    public static boolean evaluateSpelExpression(String spelExpression, String defaultValue) {
        ExpressionParser parser = new SpelExpressionParser();
        Expression expression = parser.parseExpression(spelExpression);
        Object evaluatedValue = expression.getValue();
        return evaluatedValue.toString().equals(defaultValue);
    }

    public static void main(String[] args) {
        String spelExpression1 = "'my default system property value'";
        String defaultValue1 = "my default system property value";
        System.out.println(evaluateSpelExpression(spelExpression1, defaultValue1)); // Output: true

        String spelExpression2 = "5 * 3";
        String defaultValue2 = "15";
        System.out.println(evaluateSpelExpression(spelExpression2, defaultValue2)); // Output: true

        String spelExpression3 = "new java.util.Date()";
        String defaultValue3 = "2022-01-01";
        System.out.println(evaluateSpelExpression(spelExpression3, defaultValue3)); // Output: false
    }
}
