from typing import Optional
import jwt
from rest_framework.views import APIView
from rest_framework.response import Response
from rest_framework import status

def validate_token(token: str, JWT_SECRET: str, JWT_SIGN_ALGORITHM: str) -> Optional[str]:
    try:
        decoded_token = jwt.decode(token, JWT_SECRET, algorithms=[JWT_SIGN_ALGORITHM])
        return decoded_token.get('user')
    except jwt.ExpiredSignatureError:
        return None
    except jwt.InvalidTokenError:
        return None

class LoginView(APIView):

    def post(self, request, format=None):
        data = request.data

        username = data.get('username', None)
        password = data.get('password', None)

        # Validate user credentials (assume a function validate_credentials(username, password) is available)
        if validate_credentials(username, password):
            token = request.headers.get('Authorization', None)
            if token:
                token = token.split(' ')[1]  # Extract token from "Bearer <token>"
                username_from_token = validate_token(token, JWT_SECRET, JWT_SIGN_ALGORITHM)
                if username_from_token:
                    # Token is valid, return success message with user information
                    return Response({'message': 'Login successful', 'user': username_from_token}, status=status.HTTP_200_OK)
                else:
                    # Invalid or expired token
                    return Response({'error': 'Invalid or expired token'}, status=status.HTTP_401_UNAUTHORIZED)
            else:
                # Token not provided in headers
                return Response({'error': 'Token not provided'}, status=status.HTTP_400_BAD_REQUEST)
        else:
            # Invalid credentials
            return Response({'error': 'Invalid username or password'}, status=status.HTTP_401_UNAUTHORIZED)
```

