        print(f"Sub-breeds of {self.breed_name}: {', '.join(self.sub_breeds)}")


class SubBreedAdmin:
    def __init__(self):
        self.registered_breeds = {}

    def register(self, breed_class, admin_class):
        self.registered_breeds[breed_class] = admin_class

    def display_registered_breeds(self):
        for breed_class, admin_class in self.registered_breeds.items():
            print(f"Registered breed: {breed_class.__name__}, Admin class: {admin_class.__name__}")


# Register DogBreed with SubBreedAdmin
admin = SubBreedAdmin()
admin.register(DogBreed, SubBreedAdmin)

