golden_retriever.register_sub_breed("English Cream")
golden_retriever.register_sub_breed("American")

# Display the sub-breeds of Golden Retriever
golden_retriever.display_sub_breeds()

# Display the registered breeds with their admin classes
admin.display_registered_breeds()
