```python
def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def categorize_primes(range_start, range_end):
    single_digit_primes = []
    multi_digit_primes = []
    for num in range(range_start+1, range_end):
        if is_prime(num):
            if num < 10:
                single_digit_primes.append(num)
            else:
                multi_digit_primes.append(num)
    return single_digit_primes, multi_digit_primes

# Test the function
