```Python
import math

class Tetrahedron:
    def __init__(self, sides: list):
        self.__sides = sides

    @property
    def sides(self):
        return self.__sides 

    @sides.setter
    def sides(self, sides: list):
        for side in sides:
            if side <= 0:
                raise ValueError("Side length must be a positive number.")
        self.__sides = sides

    def is_valid(self):
        return len(self.sides) == 4 and all(self.sides[0] == side for side in self.sides)

    def is_equilateral(self):
        return self.is_valid() and all(self.sides[0] == side for side in self.sides)

    def calculate_volume(self):
        if not self.is_valid():
            raise ValueError("The sides do not form a regular tetrahedron.")
        return (self.sides[0]**3) / (6 * math.sqrt(2))

    def calculate_surface_area(self):
        if not self.is_valid():
            raise ValueError("The sides do not form a regular tetrahedron.")
        return math.sqrt(3) * (self.sides[0]**2)


# Testing the class
try:
    t = Tetrahedron([3, 3, 3, 3])
    print("Volume:", t.calculate_volume())
    print("Surface Area:", t.calculate_surface_area())
    print("Is Valid:", t.is_valid())
    print("Is Equilateral:", t.is_equilateral())
except Exception as e:
    print(str(e))
```

