def minPath(grid, k):
    # Helper function to get neighbors of a cell
    def get_neighbors(r, c):
        neighbors = []
        for x, y in [(r - 1, c), (r + 1, c), (r, c - 1), (r, c + 1)]:
            if 0 <= x < len(grid) and 0 <= y < len(grid[0]):
                neighbors.append((x, y))
        return neighbors

    # Helper function to perform DFS
    def dfs(r, c, path, k):
        if k == 1:
            ans.append(path.copy())
            return
        for x, y in get_neighbors(r, c):
            if (x, y) not in path:
                path.append((x, y))
                dfs(x, y, path, k - 1)
                path.pop()

    ans = []
    for r in range(len(grid)):
        for c in range(len(grid[0])):
            dfs(r, c, [(r, c)], k)

    # Get the sequence values
    ans_values = []
    for seq in ans:
        ans_values.append([grid[r][c] for r, c in seq])

    return min(ans_values, key=lambda x: sum(x))

