def find_divisible_by_five(binary_list):
    result = []
    for binary in binary_list:
        if isinstance(binary, str):
            try:
                decimal = int(binary, 2)
                if decimal % 5 == 0:
                    result.append(binary)
            except ValueError:
                print(f"Invalid binary number: {binary}")
                continue
    return result

