class SecretNumber {
private:
    int secret;
    std::pair<int, int> range;
    int guessCount;

public:
    SecretNumber(int min, int max) : range(std::make_pair(min, max)), guessCount(0) {
        srand(static_cast<unsigned int>(time(nullptr)));
        secret = rand() % (max - min + 1) + min;
