    def terminate_daemon(self, pid):
        for proc in self.daemon_processes:
            if proc.pid == pid:
                proc.terminate()
                self.daemon_processes.remove(proc)
                return

    def check_status(self, pid):
        for proc in self.daemon_processes:
            if proc.pid == pid:
                if proc.poll() is None:
                    return "Running"
                else:
                    return "Terminated"
        return "PID not found"
