import java.util.Base64;

class ValidationException extends Exception {
    public ValidationException(String message) {
        super(message);
    }
}

public class PayloadValidator {
    public static void validatePayload(String text, int maxSize) throws ValidationException {
        byte[] decodedPayload;
        try {
            decodedPayload = Base64.getDecoder().decode(text);
        } catch (IllegalArgumentException e) {
            throw new ValidationException(e.getMessage());
        }

        if (decodedPayload.length > maxSize) {
            throw new ValidationException(String.format("Payload exceeded max size = %d bytes", maxSize));
        }
    }

    public static void main(String[] args) {
        try {
            validatePayload("SGVsbG8gV29ybGQ=", 10); // No exception thrown
            validatePayload("SGVsbG8gV29ybGQ=", 5);  // Throws ValidationException
        } catch (ValidationException e) {
            System.out.println(e.getMessage());
        }
    }
}
