def resolveConflicts(input_configurations: List[Dict[str, Union[str, List[str]]]]) -> List[Dict[str, Union[str, List[str]]]]:
    active_configurations = []
    resolved_configurations = []

    for config in input_configurations:
        conflicts = [c for c in active_configurations if c["subcategory"] in config["allowedconflicts"]]
        if not conflicts:
            active_configurations.append(config)
            resolved_configurations.append(config)
        else:
            conflicting_modes = [c for c in conflicts if c["mode"] == config["mode"]]
            if conflicting_modes:
