class Node:
    def __init__(self, value):
        self.value = value
        self.next = None

class LinkedListStack:
    def __init__(self):
        self.head = None

    def push(self, value):
        new_node = Node(value)
        new_node.next = self.head
        self.head = new_node

    def pop(self):
        if self.head is None:
            return None
        else:
            popped_value = self.head.value
            self.head = self.head.next
            return popped_value

    def peek(self):
        if self.head is None:
            return None
        else:
            return self.head.value

    def is_empty(self):
        return self.head is None

if __name__ == '__main__':
