import numpy as np

def final_grid(initial_grid, operations):
    grid = np.array(initial_grid)

    for operation in operations:
        op_name, *args = operation
        if op_name == 'transit':
            n, indices, values = args
            for i, idx in enumerate(indices):
                grid = np.roll(grid, n, axis=None)
                grid.flat[idx] = values[i]
        elif op_name == 'inverse':
            m, k = args
            for i in range(0, grid.shape[0], k):
                for j in range(0, grid.shape[1], k):
                    subgrid = grid[i:i+m, j:j+m]
                    grid[i:i+m, j:j+m] = np.flip(subgrid, (0, 1))
        elif op_name == 'magnitude':
            p, indices, factor = args
            for idx in indices:
                grid.flat[idx] = abs(grid.flat[idx] * factor)
        elif op_name == 'sin':
            q, r, a, b = args
            subgrid = grid[r:r+q, r:r+q]
            grid[r:r+q, r:r+q] = np.sin(a * subgrid + b)

    return grid.tolist()
