class PrimeGenerator:
    def __init__(self):
        self.primes = [2]  # Initialize with the first prime number

    def is_prime(self, n):
        for prime in self.primes:
            if n % prime == 0:
                return False
            if prime * prime > n:
                break
        return True

    def generate_primes(self, limit):
        sieve = [True] * (limit + 1)
        for num in range(3, int(limit**0.5) + 1, 2):
            if sieve[num]:
                for multiple in range(num * num, limit + 1, 2 * num):
                    sieve[multiple] = False
        self.primes = [2] + [i for i in range(3, limit + 1, 2) if sieve[i]]

    def newmeth(self, start):
        if start <= 2:
            return 2
        if start % 2 == 0:
            start += 1
        while True:
            if self.is_prime(start):
                return start
            start += 2
        return start

