from imblearn.over_sampling import SMOTE
from imblearn.under_sampling import RandomUnderSampler
from imblearn.pipeline import make_pipeline
from sklearn.base import BaseEstimator, ClassifierMixin

class ResampleClassifier(BaseEstimator, ClassifierMixin):
    def __init__(self, base_classifier, resampling_technique):
        self.base_classifier = base_classifier
        self.resampling_technique = resampling_technique
        self.pipeline = None

    def fit(self, X, y):
        if self.resampling_technique == 'oversampling':
            self.pipeline = make_pipeline(SMOTE(), self.base_classifier)
        elif self.resampling_technique == 'undersampling':
            self.pipeline = make_pipeline(RandomUnderSampler(), self.base_classifier)
