    public boolean fourElementsProductEqualsOne(List<Float> l) {
        int n = l.size();
        if(n < 4) return false;
        // Generate all possible combinations of four elements
        for (int i = 0; i < n - 3; i++) {
            for (int j = i + 1; j < n - 2; j++) {
                for (int k = j + 1; k < n - 1; k++) {
                    for (int m = k + 1; m < n; m++) {
