public class SetOperator {
    private Set<Integer> set;

    public SetOperator(int[] data) {
        Set<Integer> tempSet = new HashSet<>();
        for (int value : data) {
            if (!tempSet.add(value)) {
                throw new IllegalArgumentException("Error: Duplicate values are not allowed.");
            }
        }
        set = tempSet;
    }

    public boolean isSubset(int[] subset) {
        Set<Integer> subsetSet = new HashSet<>();
        for (int value : subset) subsetSet.add(value);

