class Account {
    private double balance;

    public Account(double balance) {
        this.balance = balance;
    }

    public void deposit(double amount) {
        balance += amount;
        System.out.println("Deposit successful. Current balance: " + balance);
    }

    public void withdraw(double amount) throws InsufficientBalanceException {
        if (balance < amount) {
            throw new InsufficientBalanceException("Insufficient balance. Cannot withdraw " + amount);
        }
        balance -= amount;
        System.out.println("Withdrawal successful. Current balance: " + balance);
    }
}

public class Main {
    public static void main(String[] args) {
        Account account = new Account(1000);   // initial balance

        try {
            account.deposit(500);  //Increase balance by 500
            account.withdraw(2000); //Try to withdraw 2000
        } 
        catch (InsufficientBalanceException e) {
            System.err.println(e.getMessage());
        } 
        finally {
            System.out.println("End of transactions.");
        }

    }
