      throw runtime_error("Queue is Empty");
    }
    return s1.top();
  }

  int rear() {
    if(isEmpty()) {
      throw runtime_error("Queue is Empty");
    }
    while(!s1.empty()) {
      s2.push(s1.top());
      s1.pop();
    }
    int val = s2.top();
    while(!s2.empty()) {
      s1.push(s2.top());
      s2.pop();
    }
    return val;
  }

  bool isFull() {
