#include<mutex>
#include<condition_variable>

std::mutex mtx;
std::condition_variable cv;
bool ready = false;

void read_from_file() {
    std::lock_guard<std::mutex> lck(mtx);
    std::fstream file ("data.bin", std::ios::binary | std::ios::in);
    if(!file) {
        std::cout << "Failed to read from the binary file." << std::endl;
