    cv.wait(lck, []{return ready;});
    std::ofstream file("text.txt");
    if(!file) {
        std::cout << "Failed to write to the text file." << std::endl;
    }
    else {
        file << "Successfully wrote to the text file.";
        std::cout << "Wrote to the text file." << std::endl;
    }
    file.close();
    ready = false;
}

void do_math() {
    std::unique_lock<std::mutex> lck(mtx);
    cv.wait(lck, []{return !ready;});
    try {
        double result = std::sqrt(4.0);
        std::cout << "Math operation successful. Result: " << result << std::endl;
    }
    catch(std::exception& e) {
        std::cout << "Math operation failed: " << e.what() << std::endl;
    }
}

void master_thread() {
