}

int main() {
    std::thread thread1(read_from_file);
    std::thread thread2(write_to_file);
    std::thread thread3(do_math);
    std::thread thread4(master_thread);

    thread1.join();
    thread2.join();
    thread3.join();
    thread4.join();

