vector<double> findMode(vector<double>& nums, int size) {
    map<double, int> freq;

    for(int i = 0; i < size; i++)
        freq[nums[i]]++;

    vector<double> mode;
    int max_count = 0;
    for(auto i = freq.begin(); i != freq.end(); i++) {
        if(i->second > max_count) {
            mode.clear();
            mode.push_back(i->first);
            max_count = i->second;
        }
        else if(i->second == max_count)
            mode.push_back(i->first);
