def Jacobsthal_Lucas(n, m=0):
    # Input validation
    if not isinstance(n, int) or n < 0:
        raise ValueError('n should be a positive integer')
    if not isinstance(m, int) or m < 0:
        raise ValueError('m should be a positive integer')

    # Base cases
    if n == 0:
        return 2
    if n == 1:
        return 1
    if n == 2:
        return 5

    # Recursive case
    cache = [2, 1, 5] + [0] * (n-2)
    for i in range(3, n+1):
        cache[i] = (2 * cache[i-1] + cache[i-2])
        if m:
            cache[i] %= m
    return cache[n]
