def generate_dict_seq(N, keys=None, values=None):
    if keys is None:
        keys = range(N)
    else:
        keys = list(set(keys))  # Remove any duplicate keys

    if values is None:
        values = [None]*N
    else:
        values = list(set(values))  # Remove any duplicate values

    generated_dicts = []
