public class Fib4 {

    // Calculate the n-th fib4 number.
    public static long fib4(int n) {
        if (n < 2) return 0;
        if (n == 2) return 2;

        long[] fib = new long[n+1];
        fib[2] = 2;

        for (int i = 4; i <= n; i++) {
            fib[i] = fib[i-1] + fib[i-2] + fib[i-3] + fib[i-4];
        }

        return fib[n];
