        long[] fib = new long[n];
        if (n > 0) fib[0] = 0;
        if (n > 1) fib[1] = 0;
        if (n > 2) fib[2] = 2;

        for (int i = 3; i < n; i++) {
            fib[i] = fib[i-1] + fib[i-2] + fib[i-3] + fib[i-4];
        }

        return fib;
    }

    // Find a number in the fib4 sequence.
    public static int fib4Find(long target) {
        if (target == 0) return 0;

        int i = 0;
        long fib = 0;

        while (fib < target) {
            fib = fib4(i++);
        }

        if (fib == target) return i-1;
        else return -1;
    }
}
