public abstract class Spacecraft {
    private String name;
    private double mass;
    private double speed;

    public Spacecraft(String name, double mass, double speed) {
        this.name = name;
        this.mass = mass;
        this.speed = speed;
    }

    public abstract void launch();
}

// child class InterstellarSpacecraft
public class InterstellarSpacecraft extends Spacecraft {
    private ArrayList<String> cargo;

    public InterstellarSpacecraft(String name, double mass, double speed) {
        super(name, mass, speed);
        this.cargo = new ArrayList<>();
    }

