#include <iostream>
#include <string>
#include <exception>

// Custom exception class to handle command execution errors
class CommandExecutionException : public std::exception {
private:
    std::string message; // Error message associated with the exception

public:
    // Constructor that accepts a message parameter
    CommandExecutionException(const std::string& msg) : message(msg) {}

    // Override the what() method to provide the error message
    virtual const char* what() const noexcept override {
        return message.c_str(); // Return the error message as a C-string
    }
};

class Command {
public:
    // Method to simulate command execution, which may throw an exception
    void execute() {
        // Simulate an error during command execution
        throw CommandExecutionException("Error executing command: Invalid input");
    }
};

int main() {
    try {
        Command cmd;  // Create a Command object
        cmd.execute(); // Attempt to execute the command
    } catch (const CommandExecutionException& ex) {
        // Handle the CommandExecutionException and print the error message
        std::cerr << "Command execution error: " << ex.what() << std::endl;
    }
    return 0; // Indicate that the program has completed successfully
}
``` 

