```cpp
#include <SFML/Network.hpp>
#include <iostream>
#include <thread>

/**
 * Server-Client Communication System using SFML
 * 
 * This program demonstrates a simple network communication 
 * between a server and client using SFML's networking capabilities.
 */

/**
 * Server function to handle incoming client connections
 * Listens on port 55001 and sends a welcome message to connected clients
 */
void runServer() {
    // Create a TCP listener socket bound to port 55001
    sf::TcpListener listener;

    // Attempt to listen on the specified port
    if (listener.listen(55001) != sf::Socket::Done) {
        std::cerr << "Failed to listen on port 55001" << std::endl;
        return;
    }

    std::cout << "Server is running and waiting for connections..." << std::endl;

    // Continuous loop to accept client connections
    while (true) {
        // Socket to represent the connecting client
        sf::TcpSocket client;

        // Wait for and accept a client connection
        if (listener.accept(client) == sf::Socket::Done) {
            // Prepare welcome message
            std::string message = "Welcome to the server!";

            // Send welcome message to the connected client
            if (client.send(message.c_str(), message.length() + 1) != sf::Socket::Done) {
                std::cerr << "Failed to send message to client" << std::endl;
            } else {
                std::cout << "Sent welcome message to client" << std::endl;
            }
        }
    }
}

/**
 * Client function to connect to the server and receive a message
 */
void runClient() {
    // Create a TCP socket for client connection
    sf::TcpSocket socket;

    // Attempt to connect to the server at localhost on port 55001
    if (socket.connect("127.0.0.1", 55001) != sf::Socket::Done) {
        std::cerr << "Failed to connect to server" << std::endl;
        return;
    }

    std::cout << "Connected to server" << std::endl;

    // Buffer to store received message
    char buffer[100];
    std::size_t received;

    // Attempt to receive message from server
    if (socket.receive(buffer, sizeof(buffer), received) == sf::Socket::Done) {
        // Display the received message
        std::cout << "Received: " << buffer << std::endl;
    } else {
        std::cerr << "Failed to receive message from server" << std::endl;
    }
}

/**
 * Main function to demonstrate server-client communication
 * Runs server and client in separate threads
 */
int main() {
    try {
        // Create threads for server and client
        std::thread serverThread(runServer);
        std::thread clientThread(runClient);

        // Wait for both threads to complete
        serverThread.join();
        clientThread.join();
    }
    catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
```

