```java
import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.List;

public class AnagramFinder {

    /**
     * Finds and returns all lexemes that are anagrams of each other from the input array.
     * 
     * @param lexemes An array of strings to be examined for anagrams.
     * @return An array of strings containing only the lexemes that are anagrams.
     */
    public static String[] findAnagrams(String[] lexemes) {
        // A map to hold sorted character sequences as keys and the list of corresponding lexemes as values.
        HashMap<String, List<String>> anagramMap = new HashMap<>();

        for (String lexeme : lexemes) {
            if (lexeme.length() >= 2) { // Consider only lexemes with 2 or more characters
                // Sort the characters of the lexeme and convert it back to a String
                char[] charArray = lexeme.toCharArray();
                Arrays.sort(charArray);
                String sortedLexeme = new String(charArray);

                // Group lexemes by their sorted character representation
                anagramMap.putIfAbsent(sortedLexeme, new ArrayList<>());
                anagramMap.get(sortedLexeme).add(lexeme);
            }
        }

        // Create a list to hold the resultant anagrams
        ArrayList<String> resultantArray = new ArrayList<>();

        // Collect all anagram groups with more than one lexeme
        for (List<String> anagramGroup : anagramMap.values()) {
            if (anagramGroup.size() > 1) { // Only include groups with actual anagrams
                resultantArray.addAll(anagramGroup);
            }
        }

        // Convert the list of anagrams to an array and return
        return resultantArray.toArray(new String[0]);
    }
}
```

