        anagramGroups.computeIfAbsent(sortedKey, k -> new ArrayList<>()).add(lexeme);
    }

    // Filter and collect only groups with multiple anagrams
    List<String> resultantArray = anagramGroups.values().stream()
        .filter(group -> group.size() > 1)
        .flatMap(List::stream)
        .collect(Collectors.toList());

    // Convert result to array and return
